import React, { useEffect, useCallback } from 'react'
import { useParams, Link } from 'react-router-dom'
import Board from '../components/board/Board'
import Button from '../components/common/Button'
import { useToast } from '../components/common/Toast'
import useBoardStore from '../store/boardStore'
import { supabase } from '../lib/supabaseClient'

const BoardPage = () => {
  const { slug } = useParams()
  const { showToast, ToastComponent } = useToast()
  const { 
    currentBoard, 
    fetchBoardData, 
    isLoading, 
    error 
  } = useBoardStore()

  const handleFetchBoardData = useCallback(() => {
    if (slug) {
      fetchBoardData(slug)
    }
  }, [slug, fetchBoardData])

  useEffect(() => {
    handleFetchBoardData()
  }, [handleFetchBoardData])

  useEffect(() => {
    if (!currentBoard) return

    console.log(`Setting up realtime subscription for board: ${currentBoard.name}`)

    // Supabase Realtime 구독 설정 (디바운싱 적용)
    let updateTimeout = null

    const debouncedUpdate = () => {
      if (updateTimeout) clearTimeout(updateTimeout)
      updateTimeout = setTimeout(() => {
        console.log('Fetching updated board data...')
        handleFetchBoardData()
      }, 500) // 500ms 디바운싱
    }

    const channel = supabase
      .channel(`board-${currentBoard.id}`)
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'columns',
          filter: `board_id=eq.${currentBoard.id}`
        },
        (payload) => {
          console.log('Column change received:', payload.eventType, payload.new || payload.old)
          debouncedUpdate()
        }
      )
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'cards'
        },
        (payload) => {
          console.log('Card change received:', payload.eventType, payload.new || payload.old)
          // 카드 변경은 특정 보드의 컬럼에 속한 카드만 필터링
          if (payload.new && payload.new.column_id) {
            debouncedUpdate()
          } else if (payload.old && payload.old.column_id) {
            debouncedUpdate()
          }
        }
      )
      .subscribe((status) => {
        console.log('Realtime subscription status:', status)
      })

    return () => {
      console.log('Cleaning up realtime subscription')
      if (updateTimeout) clearTimeout(updateTimeout)
      supabase.removeChannel(channel)
    }
  }, [currentBoard, handleFetchBoardData])

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-lg text-gray-600">보드를 불러오는 중...</div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="text-lg text-red-600 mb-4">
            오류가 발생했습니다: {error}
          </div>
          <div className="space-x-4">
            <Button onClick={handleFetchBoardData}>
              다시 시도
            </Button>
            <Link to="/">
              <Button variant="secondary">
                홈으로 돌아가기
              </Button>
            </Link>
          </div>
        </div>
      </div>
    )
  }

  if (!currentBoard) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="text-lg text-gray-600 mb-4">
            보드를 찾을 수 없습니다.
          </div>
          <Link to="/">
            <Button>홈으로 돌아가기</Button>
          </Link>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* 네비게이션 바 */}
      <nav className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-4">
              <Link 
                to="/"
                className="text-gray-600 hover:text-gray-900 transition-colors"
              >
                ← 보드 목록
              </Link>
              <div className="text-gray-300">|</div>
              <h1 className="text-lg font-semibold text-gray-900">
                {currentBoard.name}
              </h1>
            </div>

            <div className="flex items-center space-x-2">
              <span className="text-sm text-gray-500">
                실시간 동기화 활성화
              </span>
              <div className="w-2 h-2 bg-green-500 rounded-full"></div>
            </div>
          </div>
        </div>
      </nav>

      {/* 보드 콘텐츠 */}
      <Board />
    </div>
  )
}

export default BoardPage
