import React, { useState } from 'react'
import {
  DndContext,
  DragOverlay,
  closestCorners,
  KeyboardSensor,
  PointerSensor,
  useSensor,
  useSensors,
} from '@dnd-kit/core'
import { arrayMove, SortableContext, horizontalListSortingStrategy } from '@dnd-kit/sortable'
import Column from './Column'
import Card from './Card'
import Button from '../common/Button'
import useBoardStore from '../../store/boardStore'

const Board = () => {
  const { 
    currentBoard, 
    columns, 
    cards,
    addColumn, 
    moveCard,
    updateCardPositions,
    isLoading,
    error 
  } = useBoardStore()

  const [activeCard, setActiveCard] = useState(null)
  const [isAddingColumn, setIsAddingColumn] = useState(false)
  const [newColumnTitle, setNewColumnTitle] = useState('')

  const sensors = useSensors(
    useSensor(PointerSensor),
    useSensor(KeyboardSensor)
  )

  const handleDragStart = (event) => {
    const { active } = event
    const card = cards.find(card => card.id === active.id)
    setActiveCard(card)
  }

  const handleDragEnd = async (event) => {
    const { active, over } = event
    setActiveCard(null)

    if (!over) return

    const activeCard = cards.find(card => card.id === active.id)
    if (!activeCard) return

    const overId = over.id
    const overColumn = columns.find(col => col.id === overId)
    const overCard = cards.find(card => card.id === overId)

    let newColumnId = activeCard.column_id
    let newPosition = activeCard.position

    if (overColumn) {
      // 컬럼 위에 드롭된 경우 - 해당 컬럼의 마지막 위치에 추가
      newColumnId = overColumn.id
      const columnCards = cards.filter(card => card.column_id === overColumn.id && card.id !== activeCard.id)
      newPosition = columnCards.length
    } else if (overCard && overCard.id !== activeCard.id) {
      // 다른 카드 위에 드롭된 경우
      newColumnId = overCard.column_id
      const columnCards = cards
        .filter(card => card.column_id === overCard.column_id && card.id !== activeCard.id)
        .sort((a, b) => a.position - b.position)

      const overIndex = columnCards.findIndex(card => card.id === overCard.id)
      newPosition = overIndex >= 0 ? overIndex : columnCards.length
    }

    // 실제로 위치가 변경된 경우에만 업데이트
    if (newColumnId !== activeCard.column_id || newPosition !== activeCard.position) {
      // 메인 카드 이동
      await moveCard(activeCard.id, newColumnId, newPosition)
    }
  }

  const handleAddColumn = async () => {
    if (newColumnTitle.trim()) {
      await addColumn(newColumnTitle.trim())
      setNewColumnTitle('')
      setIsAddingColumn(false)
    }
  }

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-lg text-gray-600">로딩 중...</div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-lg text-red-600">오류: {error}</div>
      </div>
    )
  }

  if (!currentBoard) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-lg text-gray-600">보드를 찾을 수 없습니다.</div>
      </div>
    )
  }

  return (
    <div className="h-full flex flex-col">
      {/* 보드 헤더 */}
      <div className="flex-shrink-0 p-6 border-b border-gray-200">
        <h1 className="text-2xl font-bold text-gray-900">
          {currentBoard.name}
        </h1>
      </div>

      {/* 보드 콘텐츠 */}
      <div className="flex-1 p-6 overflow-x-auto">
        <DndContext
          sensors={sensors}
          collisionDetection={closestCorners}
          onDragStart={handleDragStart}
          onDragEnd={handleDragEnd}
        >
          <div className="flex space-x-6 min-h-full">
            <SortableContext
              items={columns.map(col => col.id)}
              strategy={horizontalListSortingStrategy}
            >
              {columns
                .sort((a, b) => a.position - b.position)
                .map(column => (
                  <Column key={column.id} column={column} />
                ))}
            </SortableContext>

            {/* 컬럼 추가 */}
            <div className="flex-shrink-0 w-80">
              {isAddingColumn ? (
                <div className="bg-gray-100 rounded-lg p-4 space-y-3">
                  <input
                    type="text"
                    value={newColumnTitle}
                    onChange={(e) => setNewColumnTitle(e.target.value)}
                    placeholder="컬럼 제목을 입력하세요..."
                    className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    autoFocus
                    onKeyPress={(e) => {
                      if (e.key === 'Enter') {
                        handleAddColumn()
                      }
                    }}
                  />
                  <div className="flex space-x-2">
                    <Button 
                      size="small" 
                      onClick={handleAddColumn}
                      disabled={!newColumnTitle.trim()}
                    >
                      추가
                    </Button>
                    <Button 
                      size="small" 
                      variant="secondary" 
                      onClick={() => {
                        setIsAddingColumn(false)
                        setNewColumnTitle('')
                      }}
                    >
                      취소
                    </Button>
                  </div>
                </div>
              ) : (
                <Button
                  variant="outline"
                  onClick={() => setIsAddingColumn(true)}
                  className="w-full h-12"
                >
                  + 컬럼 추가
                </Button>
              )}
            </div>
          </div>

          <DragOverlay>
            {activeCard ? <Card card={activeCard} /> : null}
          </DragOverlay>
        </DndContext>
      </div>
    </div>
  )
}

export default Board
